import {request, error, loading} from "./useRequest"

const useMarvelService = () => {

    const getCharacter = (id) => {
        const result = request(`https://gateway.marvel.com:443/v1/public/characters/${id}?apikey=b9cd5ff800088663996fca66ed987b75`)
        return result
    }

    return {error, loading, getCharacter}
}

export default useMarvelService
