import { useState, useEffect } from 'react';

import useMarvelService from '../../services/useMarvelService'
import Spinner from '../Spinner/Spinner';

import './charList.scss';

const CharList = () => {
    const [charList, setCharList] = useState([])
    const [offset, setOffset] = useState(210)

    const { loading, getAllCharacters } = useMarvelService()

    useEffect(() => {
        onCharList()
    }, [])

    const onCharList = (num) => {
        getAllCharacters().then(res => setCharList(...charList, res))
        setOffset(offset => (offset + num))
    }

    console.log(offset)

    const isLoading = loading ? <Spinner /> : null
    const characters = charList.map(char => <Character 
                                                key={char.id}
                                                char={char}/>)

    return (
        <div className="char__list">
            <ul className="char__grid">
                {characters}
                {isLoading}
            </ul>
            <button className="button button__main button__long"
                onCharList={() => onCharList(9)}>
                <div className="inner">load more</div>
            </button>
        </div>
    )
}

const Character = ({char}) => {
    return (
        <li className="char__item">
            <img src={char.avatar} alt="abyss" />
            <div className="char__name">{char.name}</div>
        </li>
    )
}

export default CharList;
