import { useState, useEffect } from 'react';

import useMarvelService from '../../services/useMarvelService';
import Spinner from '../Spinner/Spinner';
import Error from '../Error/Error'


import './randomChar.scss';
import thor from '../../resources/img/thor.jpeg';
import mjolnir from '../../resources/img/mjolnir.png';

const RandomChar = () => {
    const [character, setCharacter] = useState(null)

    const {getCharacter, loading, error} = useMarvelService()

    useEffect(() => {
        setCharacter()
    }, [])

    const onCharacter = () => {
        getCharacter().then(res => setCharacter(res))
    }

    console.log(character)

    const isLoading = loading ? <Spinner /> : null
    const isError = error ? <Error /> : null
    const content = character ? <View/> : null

    return (
        <div className="randomchar">
            <div className="randomchar__block">

            </div>
            <div className="randomchar__static">
                <p className="randomchar__title">
                    Random character for today!<br/>
                    Do you want to get to know him better?
                </p>
                <p className="randomchar__title">
                    Or choose another one
                </p>
                <button 
                className="button button__main"
                onClick={onCharacter}>
                    <div className="inner">try it</div>
                </button>
                <img src={mjolnir} alt="mjolnir" className="randomchar__decoration"/>
            </div>
        </div>
    )
}

const View = ({char}) => {
    <>
     <img src={character.avatar} alt="Random character" className="randomchar__img"/>
                <div className="randomchar__info">
                    <p className="randomchar__name">Thor</p>
                    <p className="randomchar__descr">
                        As the Norse God of thunder and lightning, Thor wields one of the greatest weapons ever made, the enchanted hammer Mjolnir. While others have described Thor as an over-muscled, oafish imbecile, he's quite smart and compassionate...
                    </p>
                    <div className="randomchar__btns">
                        <a href="#" className="button button__main">
                            <div className="inner">homepage</div>
                        </a>
                        <a href="#" className="button button__secondary">
                            <div className="inner">Wiki</div>
                        </a>
                    </div>
                </div>
                </>

}



export default RandomChar;
