import useRequest from "./useRequest"

const useMarvelService = () => {

    const {loading, error, request} = useRequest()

    const getCharacter = async (id) => {
        const result = await request(`https://gateway.marvel.com:443/v1/public/character/${id}?apikey=b9cd5ff800088663996fca66ed987b75`)
        return result
    }

    const getRandomCharacter = async () => {
        const randomId = Math.floor(Math.random() * (1011400 - 1011000) + 1011000)
        // const randomId = 1011334

        const result = await request(`https://gateway.marvel.com:443/v1/public/characters/${randomId}?apikey=b9cd5ff800088663996fca66ed987b75`)
        console.log(result)
        modifiedCharacter(result.data.results[0])
        return result
    }

    const modifiedCharacter = (char) => {
        return {
            name: char.name,
            id: char.id,
            descr: char.descr,
            avatar: `${char.thumbnail.path}.${char.thumbnail.path}`
        }
    }

    return {error, loading, getCharacter, getRandomCharacter}
}

export default useMarvelService
